// predefined values
const mainDivId = 'wk-item-difficulty-main'
const colors = {easy: '#35bf28', norm: '#f0be35', hard: '#e67615', extr: '#e63535', none: '#7a7a7a'};

// WaniKani Item Info Injector
const wkItemInfo = window.wkItemInfo;

// functions for later
const getItemDiff = (char, dict) => Object.values(dict).findIndex(s => s.includes(char));
const diffToColor = diff => colors[diff > 50 ? 'extr' : (diff > 25 ? 'hard' : (diff >= 10 ? 'norm' : 'easy'))];
const diffToStr = diff => Math.max(diff/10, 0).toFixed(1) + '/10';
const innerDiffDiv = color => `<div style="width: 24px; height: 24px; border-radius: 12px; margin: 13px; position: absolute; box-shadow: 0 0 6px 6px ${color}; opacity: 0.7"></div><div style="width: 26px; height: 26px; background: ${color}; border-radius: 13px; margin: 12px; position: absolute"></div>`;
const diffDiv = (color, hovertext, absolute) => `<div id="${mainDivId}" title="${hovertext}" style="width: 50px; height: 50px; background: #5f5f5f; border-radius: 10px; box-shadow: 4px 4px 3px 1px rgba(0,0,0,0.3); position: ${absolute ? 'absolute; bottom: 10px; right: 10px' : 'relative'}">${innerDiffDiv(color)}</div>`;
const divByChar = (type, char, absolute) => {
    let diff = type == 'kanji' ? getItemDiff(char, kanjiDiff) : (type == 'vocabulary' ? getItemDiff(char, vocabDiff) : undefined);
    let color = diffToColor(diff);
    return diffDiv(color, diffToStr(diff), absolute);
};
const strToElement = str => {
    var temp = document.createElement('div');
    temp.innerHTML = str;
    return temp.firstChild;
}

// main function
(async () => {

    // find out if page is lesson, review, or word info
    const pageType = window.location.pathname.includes('lesson') ? 'lesson' : (window.location.pathname.includes('review') ? 'review' : (window.location.pathname.includes('extra_study') ? 'extra_study' : 'info'));

    // "await existence of element"-function
    const awaitElement = (id) => new Promise(resolve => { // wait until character element exists
        if (document.getElementById(id)) return resolve(document.getElementById(id));
        const observer = new MutationObserver(() => { // observer for existence
            if (document.getElementById(id)) {
                resolve(document.getElementById(id));
                observer.disconnect();
            }
        });
        observer.observe(document.body, { childList: true, subtree: true });
    });

    if (pageType == 'info') { // kanji/vocab/radical info page
        wkItemInfo.append('Difficulty', ({type, characters}) => strToElement(divByChar(type, characters, false)));
    } else { // extra study, lesson, or review page
        const characterElement = await awaitElement(pageType == 'lesson' ? 'main-info' : 'character')

        // initialize element
        characterElement.appendChild(strToElement(diffDiv('gray', 'Inactive', true)));
        const mainDiv = document.getElementById(mainDivId);

        // update diff sign
        const updateDiffInfo = async function () {
            // get item info
            const itemType = characterElement.className[0].toLowerCase();
            const itemName = characterElement.children[0].innerHTML;
            
            // differentiate based on type
            let itemDiff, hovertext;
            if (itemType == 'radical') {
                itemDiff = colors.none;
                hovertext = 'n/a';
            } else {
                itemDiff = getItemDiff(itemName, itemType == 'kanji' ? kanjiDiff : vocabDiff);
                hovertext = diffToStr(itemDiff);
                if (itemDiff == -1) {
                    itemDiff = colors.none;
                    hovertext = 'n/a';
                } else {
                    itemDiff = diffToColor(itemDiff);
                }
            }

            // renew attributes
            mainDiv.title = hovertext;
            mainDiv.innerHTML = innerDiffDiv(itemDiff);
        };

        // set diff box contents for first item
        updateDiffInfo();

        // setup observer to change info box contents for subsequent items
        var observer = new MutationObserver(() => updateDiffInfo());
        var config = { characterData: true, childList: true, subtree: true };
        observer.observe(characterElement.children[0], config);
    }
})();